    }

    public PodcastTrack getTrack(String url)
    {
      if (getTracks()!=null)
      {
        for (java.util.Iterator j = getTracks().iterator(); j.hasNext(); /* Nothing */) {
            PodcastTrack track = (PodcastTrack) j.next();
            if (track.getUrl().equals(url))
                return track;
        }
      }
      return null;
    }


